mapper.select:
SELECT seq, branchAcronym, branch, branchManager, telNo, faxNo, eMailAddress, itoTelNo, itoDsnNo, itoChief, itoAddress, dGbloc, dodac FROM branch WHERE 1 = 1
AND seq = #{seq}
AND branchAcronym = #{branchAcronym}

mapper.insert:
INSERT INTO
	branch
	(
		branchAcronym, branch, branchManager, telNo, faxNo, eMailAddress, itoTelNo, itoDsnNo, itoChief, itoAddress, dGbloc, dodac
	)	
VALUES
	(
		#{branchAcronym}, #{branch}, #{branchManager}, #{telNo}, #{faxNo}, #{eMailAddress}, #{itoTelNo}, #{itoDsnNo}, #{itoChief}, #{itoAddress}, #{dGbloc}, #{dodac}
	)

mapper.select:
SELECT seq, podAcronym, podFull, destAddress, podAgentName, remark FROM pod WHERE 1 = 1
AND seq = #{seq}
AND podAcronym = #{podAcronym}

mapper.insert:
INSERT INTO
	pod
	(
		podAcronym, podFull, destAddress, podAgentName, remark
	)
VALUES
	(
		#{podAcronym}, #{podFull}, #{destAddress}, #{podAgentName}, #{remark}
	)

mapper.select:
SELECT seq, scac, scacFullName, address, telNo, faxNo, president, manager, mainEmail, trafficEmail, agent FROM carrier WHERE 1 = 1
AND seq = #{seq}
AND scac = #{scac}

mapper.insert:
INSERT INTO
	carrier
	(
		scac, scacFullName, address, telNo, faxNo, president, manager, mainEmail, trafficEmail, agent
	)
VALUES
	(
		#{scac}, #{scacFullName}, #{address}, #{telNo}, #{faxNo}, #{president}, #{manager}, #{mainEmail}, #{trafficEmail}, #{agent}
	)

mapper.select:
SELECT seq, companyCode, companyName, companyNameAcronym, companyFullName, address, president, manager, telNo, faxNo, eMailAddress, businessLicenceNo FROM company WHERE 1 = 1
AND seq = #{seq}
AND companyCode = #{companyCode}

mapper.insert:
INSERT INTO
	company
	(
		companyCode, companyName, companyNameAcronym, companyFullName, address, president, manager, telNo, faxNo, eMailAddress, businessLicenceNo
	)
VALUES
	(
		#{companyCode}, #{companyName}, #{companyNameAcronym}, #{companyFullName}, #{address}, #{president}, #{manager}, #{telNo}, #{faxNo}, #{eMailAddress}, #{businessLicenceNo}
	)
