mapper.select:
SELECT count(*) FROM gbl_ib WHERE 1 = 1
AND area = (select code_etc from symbol where main_code = '01' and sub_code = #{area})
AND area = #{branch}
AND tsp = #{carrier}
AND code = #{code}
AND pud >= str_to_date(#{startPud}, '%Y%m%d')
AND pud <= str_to_date(#{endPud}, '%Y%m%d')

SELECT * FROM ( SELECT (@rownum := @rownum + 1) AS rownum, g.seq, g.gblNo, g.shipperName, g.rank, g.code, g.tsp, g.destAddress, g.pud, g.rdd, g.arriveDate, g.awbNo, g.ssn, g.pmjDate, g.area AS areaLocal, g.fright, g.eMailAddress, g.oblNo, g.vessle, g.blCompany, g.remark, g.eta, g.sitIn, g.sitOut, g.sitNo, g.rate, g.yjNo, g.totalPcs, g.phone, g.address, g.onHandDate, g.gbloc, g.destinationGbloc, g.itemsPieces, g.grossWeight, g.netWeight FROM gbl_ib g, (SELECT @rownum := 0) r WHERE 1 = 1
AND area = (select code_etc from symbol where main_code = '01' and sub_code = #{area})
AND area = #{branch}
AND tsp = #{carrier}
AND code = #{code}
AND pud >= str_to_date(#{startPud}, '%Y%m%d')
AND pud <= str_to_date(#{endPud}, '%Y%m%d')
ORDER BY g.seq )u WHERE rownum BETWEEN #{pagination.itemSeqBegin} AND #{pagination.itemSeqEnd}

SELECT gbl_no, gbl_input, gbl_weight, gbl_custom, gbl_onHandList, gbl_delivery, gbl_invoice FROM gbl_status WHERE gbl_no IN ( SELECT gblNo FROM gbl_ib
AND area = #{branch}
AND tsp = #{carrier}
AND code = #{code}
AND pud >= str_to_date(#{startPud}, '%Y%m%d')
AND pud <= str_to_date(#{endPud}, '%Y%m%d')
)

SELECT count(*) FROM gbl_ib g WHERE gblNo IN (select gbl_no from gbl_status where gbl_weight = 1 and gbl_custom = 0 ) AND g.seq IN ( SELECT gblSeq FROM weight_ib w WHERE w.seq NOT IN (SELECT weightSeq FROM inbound_invoice_weight WHERE gblSeq = g.seq) )
AND area = (select code_etc from symbol where main_code = '01' and sub_code = #{area})
AND area = #{branch}
AND tsp = #{carrier}
AND code = #{code}
AND pud >= str_to_date(#{startPud}, '%Y%m%d')
AND pud <= str_to_date(#{endPud}, '%Y%m%d')

SELECT * FROM ( SELECT (@rownum := @rownum + 1) AS rownum, g.seq, g.gblNo, g.shipperName, g.rank, g.code, g.tsp, g.destAddress, g.pud, g.rdd, g.arriveDate, g.awbNo, g.ssn, g.pmjDate, g.area AS areaLocal, g.fright, g.eMailAddress, g.oblNo, g.vessle, g.blCompany, g.remark, g.eta, g.sitIn, g.sitOut, g.sitNo, g.rate, g.yjNo, g.totalPcs, g.phone, g.address, g.onHandDate, g.gbloc, g.destinationGbloc, g.itemsPieces, g.grossWeight, g.netWeight FROM gbl_ib g, (SELECT @rownum := 0) r WHERE gblNo IN (select gbl_no from gbl_status where gbl_weight = 1 and gbl_custom = 0 ) AND g.seq IN ( SELECT gblSeq FROM weight_ib w WHERE w.seq NOT IN (SELECT weightSeq FROM inbound_invoice_weight WHERE gblSeq = g.seq) )
AND area = (select code_etc from symbol where main_code = '01' and sub_code = #{area})
AND area = #{branch}
AND tsp = #{carrier}
AND code = #{code}
AND pud >= str_to_date(#{startPud}, '%Y%m%d')
AND pud <= str_to_date(#{endPud}, '%Y%m%d')
ORDER BY g.seq )u WHERE rownum BETWEEN #{pagination.itemSeqBegin} AND #{pagination.itemSeqEnd}

SELECT
	count(*)
FROM
	decide_ib
WHERE
	gbl_seq = #{gblSeq}

	AND
		dd619_seq = #{dd619Seq}


	AND
		memorandum_list_seq = #{memorandumSeq}


	AND
		title = #{title}

mapper.insert:
INSERT INTO
	weight_ib
	(
		piece, type, gross, grossKg, tare, net, cuft, remark, gblSeq
	)
VALUES
	(
		#{piece}, #{type}, #{gross}, #{grossKg}, #{tare}, #{net}, #{cuft}, #{remark}, #{gblSeq}
	)

INSERT INTO
	inbound_invoice_list
	(
		gblNo, name, ssn, inboundInvoiceNo, invoiceDate, gblSeq
	)
VALUES
	(
		#{gblNo}, (select shipperName from gbl_ib where seq = #{gblSeq}), (select ssn from gbl_ib where seq = #{gblSeq}), #{inboundInvoiceNo}, #{invoiceDate}, #{gblSeq}
	)

	SELECT LAST_INSERT_ID()

INSERT INTO
	dd619_ib
	(
		gbl_seq, gbl_no, name, rank, ssn, code, carrier_shipment_reference, write_date, memorandum_list_seq
	)
VALUES
(
	#{gblSeq}, #{gblNo}, #{name}, #{rank}, #{ssn}, #{code}, #{carrierShipmentReference}, SYSDATE(), #{memorandumListSeq}
)

INSERT INTO
	decide_ib
	(
		title, cost, gbl_seq, dd619_seq, price, memorandum_list_seq
	)
VALUES
	(
		#{title}, #{cost}, #{gblSeq}, #{dd619Seq}, #{price}, #{memorandumSeq}
	)

mapper.update:
UPDATE
	decide_ib
SET

		title = #{title},



			cost = #{cost}


			price = #{price}


WHERE
	gbl_seq = #{gblSeq}

	AND
		dd619_seq = #{dd619Seq}


	AND
		memorandum_list_seq = #{memorandumSeq}


	AND
		title = #{title}

mapper.delete:
DELETE FROM
	dd619_ib
WHERE
	gbl_seq = #{gblSeq}
AND
	memorandum_list_seq = #{memorandumListSeq}
