mapper.select:
SELECT count(*) FROM gbl_ib WHERE 1 = 1
AND area = (select code_etc from symbol where main_code = '01' and sub_code = #{area})
AND area = #{branch}
AND tsp = #{carrier}
AND code = #{code}
AND pud >= str_to_date(#{startPud}, '%Y%m%d')
AND pud <= str_to_date(#{endPud}, '%Y%m%d')

SELECT * FROM ( SELECT (@rownum := @rownum + 1) AS rownum, g.seq, g.gblNo, g.shipperName, g.rank, g.code, g.tsp, g.destAddress, g.pud, g.rdd, g.arriveDate, g.awbNo, g.ssn, g.pmjDate, g.area AS areaLocal, g.fright, g.eMailAddress, g.oblNo, g.vessle, g.blCompany, g.remark, g.eta, g.sit, g.rate, g.yjNo, g.totalPcs FROM gbl_ib g, (SELECT @rownum := 0) r WHERE 1 = 1
AND area = (select code_etc from symbol where main_code = '01' and sub_code = #{area})
AND area = #{branch}
AND tsp = #{carrier}
AND code = #{code}
AND pud >= str_to_date(#{startPud}, '%Y%m%d')
AND pud <= str_to_date(#{endPud}, '%Y%m%d')
ORDER BY g.seq )u WHERE rownum BETWEEN #{pagination.itemSeqBegin} AND #{pagination.itemSeqEnd}

SELECT gbl_no, gbl_input, gbl_preperation, gbl_delivery, gbl_truckmanifast, gbl_arrival, gbl_process, gbl_bookinglist, gbl_invoice FROM gbl_status WHERE gbl_no IN ( SELECT gbl_no FROM gbl_ib WHERE 1 = 1
AND area = #{branch}
AND tsp = #{carrier}
AND code = #{code}
AND pud >= str_to_date(#{startPud}, '%Y%m%d')
AND pud <= str_to_date(#{endPud}, '%Y%m%d')
)

mapper.insert:
INSERT INTO
	weight_ib
	(
		piece, type, gross, grossKg, tare, net, cuft, remark, gblSeq
	)
VALUES
	(
		#{piece}, #{type}, #{gross}, #{grossKg}, #{tare}, #{net}, #{cuft}, #{remark}, #{gblSeq}
	)

INSERT INTO
	dd619_ib
	(
		gbl_seq, gbl_no, name, rank, ssn, code, carrier_shipment_reference, write_date, memorandum_list_seq
	)
VALUES
(
	#{gblSeq}, #{gblNo}, #{name}, #{rank}, #{ssn}, #{code}, #{carrierShipmentReference}, SYSDATE(), #{memorandumListSeq}
)

mapper.delete:
DELETE FROM
	dd619_ib
WHERE
	gbl_seq = #{gblSeq}
AND
	memorandum_list_seq = #{memorandumListSeq}
