mapper.select:
SELECT count(*) FROM gbl WHERE 1 = 1
AND area = (select code_etc from symbol where main_code = '01' and sub_code = #{area})
AND area = #{branch}
AND tsp = #{carrier}
AND code = #{code}
AND pud >= str_to_date(#{startPud}, '%Y%m%d')
AND pud <= str_to_date(#{endPud}, '%Y%m%d')

SELECT * FROM ( SELECT (@rownum := @rownum + 1) AS rownum, g.seq, g.gbl_no, g.name, g.rank, g.code, g.tsp, g.address, g.area, g.ssn, g.pud, g.rdd, g.gross, g.net, g.tare, g.cuft, g.piece, g.item_ea, g.vessel_name, g.arrive_date, g.obl_no, g.pmj_date, g.remark FROM gbl_ib g, (SELECT @rownum := 0) r WHERE 1 = 1
AND area = (select code_etc from symbol where main_code = '01' and sub_code = #{area})
AND area = #{branch}
AND tsp = #{carrier}
AND code = #{code}
AND pud >= str_to_date(#{startPud}, '%Y%m%d')
AND pud <= str_to_date(#{endPud}, '%Y%m%d')
ORDER BY g.seq )u WHERE rownum BETWEEN #{pagination.itemSeqBegin} AND #{pagination.itemSeqEnd}

mapper.insert:
INSERT INTO
	gbl
	(
		no, customer_name, rank, code, scac, origin_gblock, dest_gblock, pud, ssn, rdd, pod, poe, area, origin_address, us_no, dest_port, origin_port, origin_city, mil_svc
	)
VALUES
	(
		#{no}, #{customerName}, #{rank}, #{code}, #{scac}, #{originGBlock}, #{destGBlock}, #{pud}, #{ssn}, #{rdd}, #{pod}, #{poe}, #{areaLocal}, #{originAddress}, #{usNo}, #{destPort}, #{originPort}, #{originCity}, #{milSVC}		
	)
