mapper.select:
SELECT
	count(*)
FROM
	invoice_rate
WHERE
	tsp = #{tsp}
AND
	code = #{code}
AND
	process = #{process}

	AND
		ob_type = #{obType}

mapper.insert:
INSERT INTO invoice_rate ( tsp, code, rate, process, write_year
, ob_type
) VALUES ( #{tsp}, #{code}, #{rate}, #{process}, DATE_FORMAT(SYSDATE(), '%Y')
, #{obType}
)

mapper.update:
UPDATE
	invoice_rate
SET
	rate = #{rate}
WHERE
	tsp = #{tsp}
AND
	code = #{code}
AND
	process = #{process}
AND
	write_year = DATE_FORMAT(SYSDATE(), '%Y')

	AND
		ob_type = #{obType}
