mapper.select:
SELECT
	seq, type, subject, comment, area, articles, article_comment1, article_comment2, article_comment3, article_comment4, article_comment5, termination, sit_start_date, sit_end_date, long_carry, write_date
FROM
	memorandum
WHERE
	gbl_seq = #{gblSeq}
AND
	type = #{type}

	AND
		memorandum_list_seq = #{memorandumSeq}

SELECT
	seq, type, subject, comment, area, articles, article_comment1, article_comment2, article_comment3, article_comment4, article_comment5, gbl_seq, write_date, extra_pick_up_charge, termination, sit_start_date, sit_end_date, long_carry
FROM
	memorandum_ib
WHERE
	gbl_seq = #{gblSeq}
AND
	type = #{type}

	AND
		memorandum_list_seq = #{memorandumSeq}

mapper.insert:
INSERT INTO
	memorandum
	(
		type, subject, comment, area, articles, article_comment1, article_comment2, article_comment3, article_comment4, article_comment5, gbl_seq, write_date, memorandum_list_seq
	)
VALUES
	(
		#{type}, #{subject}, #{comment}, #{area}, #{articles}, #{articleComment1}, #{articleComment2}, #{articleComment3}, #{articleComment4}, #{articleComment5}, #{gblSeq}, SYSDATE(), #{memorandumSeq}
	)

INSERT INTO
	memorandum_ib
	(
		type, subject, comment, area, articles, article_comment1, article_comment2, article_comment3, article_comment4, article_comment5, gbl_seq, write_date, memorandum_list_seq
	)
VALUES
	(
		#{type}, #{subject}, #{comment}, #{area}, #{articles}, #{articleComment1}, #{articleComment2}, #{articleComment3}, #{articleComment4}, #{articleComment5}, #{gblSeq}, SYSDATE(), #{memorandumSeq}
	)

mapper.update:
UPDATE
	memorandum
SET


			extra_pick_up_charge = #{invoiceValue}


			termination = #{invoiceValue}


			sit_start_date = #{invoiceValue} 


			sit_end_date = #{invoiceValue}


			long_carry = #{invoiceValue}


WHERE
	gbl_seq = #{gblSeq}
AND
	memorandum_list_seq = #{memorandumSeq}
AND
	type = #{type}

UPDATE
	memorandum_ib
SET


			extra_pick_up_charge = #{invoiceValue}


			termination = #{invoiceValue}


			sit_start_date = #{invoiceValue} 


			sit_end_date = #{invoiceValue}


			long_carry = #{invoiceValue}


WHERE
	gbl_seq = #{gblSeq}
AND
	memorandum_list_seq = #{memorandumSeq}
AND
	type = #{type}

mapper.delete:
DELETE FROM
	memorandum
WHERE
	gbl_seq = #{gblSeq}
AND
	type = #{type}
AND
	memorandum_list_seq = #{memorandumSeq}

DELETE FROM
	memorandum_ib
WHERE
	gbl_seq = #{gblSeq}
AND
	type = #{type}
AND
	memorandum_list_seq = #{memorandumSeq}

DELETE FROM
	memorandum_list
WHERE
	gbl_seq = #{gblSeq}
AND
	seq = #{seq}

DELETE FROM
	memorandum_list_ib
WHERE
	gbl_seq = #{gblSeq}
AND
	seq = #{seq}
