mapper.select:
SELECT count(*) FROM gbl WHERE 1 = 1
AND area = (select code_etc from symbol where main_code = '01' and sub_code = #{area})
AND area = #{branch}
AND scac = #{carrier}
AND code = #{code}
AND pud >= str_to_date(#{startPud}, '%Y%m%d')
AND pud <= str_to_date(#{endPud}, '%Y%m%d')

SELECT * FROM ( SELECT (@rownum := @rownum + 1) AS rownum, g.seq, g.no, g.pud, g.rank, g.code, g.scac, g.customer_name, g.area, g.us_no, g.dest_port, CASE WHEN(gwc.seq = g.seq) THEN gwc.pcs ELSE '' END AS pcs, g.lbs, CASE WHEN(gwc.seq = g.seq) THEN gwc.cuft ELSE '' END AS cuft FROM gbl g LEFT OUTER JOIN ( SELECT gt.seq, COUNT(wc.piece) AS pcs, SUM(wc.gross) AS gross, SUM(wc.cuft) AS cuft FROM gbl gt JOIN weight_certificate wc ON (wc.gbl_seq = gt.seq) GROUP BY gt.seq ) gwc ON (gwc.seq = g.seq), (SELECT @rownum := 0) r WHERE 1 = 1
AND area = (select code_etc from symbol where main_code = '01' and sub_code = #{area})
AND area = #{branch}
AND scac = #{carrier}
AND code = #{code}
AND pud >= str_to_date(#{startPud}, '%Y%m%d')
AND pud <= str_to_date(#{endPud}, '%Y%m%d')
ORDER BY g.seq )u WHERE rownum BETWEEN #{pagination.itemSeqBegin} AND #{pagination.itemSeqEnd}

SELECT gbl_no, gbl_input, gbl_preperation, gbl_delivery, gbl_truckmanifast, gbl_arrival, gbl_process, gbl_bookinglist, gbl_invoice FROM gbl_status WHERE gbl_no IN ( SELECT gbl_no FROM gbl WHERE 1 = 1
AND area = #{branch}
AND scac = #{carrier}
AND code = #{code}
AND pud >= str_to_date(#{startPud}, '%Y%m%d')
AND pud <= str_to_date(#{endPud}, '%Y%m%d')
)

SELECT
	count(*)
FROM
	decide
WHERE
	gbl_seq = #{gblSeq}

	AND
		dd619_seq = #{dd619Seq}


	AND
		memorandum_list_seq = #{memorandumSeq}


	AND
		title = #{title}

mapper.insert:
INSERT INTO
	gbl
	(
		no, customer_name, rank, code, scac, origin_gblock, dest_gblock, pud, ssn, rdd, pod, poe, area, origin_address, us_no, dest_port, origin_port, origin_city, mil_svc
	)
VALUES
	(
		#{no}, #{customerName}, #{rank}, #{code}, #{scac}, #{originGBlock}, #{destGBlock}, #{pud}, #{ssn}, #{rdd}, #{pod}, #{poe}, #{areaLocal}, #{originAddress}, #{usNo}, #{destPort}, #{originPort}, #{originCity}, #{milSVC}		
	)

INSERT INTO
	dd619
	(
		gbl_seq, gbl_no, name, rank, ssn, code, carrier_shipment_reference, write_date, memorandum_list_seq
	)
VALUES
(
	#{gblSeq}, #{gblNo}, #{name}, #{rank}, #{ssn}, #{code}, #{carrierShipmentReference}, SYSDATE(), #{memorandumListSeq}
)

 INSERT INTO
 	weight_certificate
 	(
 		piece, type, status, gross, tare, net, cuft, remark, gbl_seq, date
 	)   
 VALUES
	(
		#{piece}, #{type}, #{status}, #{gross}, #{tare}, #{net}, #{cuft}, #{remark}, #{gblSeq}, #{date}
	)

    INSERT INTO
    	truck_manifast
    	(
    		date, branch, code
    	)    
    VALUES
    	(
    		SYSDATE(), #{branch}, #{code}
    	)

		SELECT LAST_INSERT_ID()

INSERT INTO
	decide
	(
		title, cost, gbl_seq, dd619_seq, price, memorandum_list_seq
	)
VALUES
	(
		#{title}, #{cost}, #{gblSeq}, #{dd619Seq}, #{price}, #{memorandumSeq}
	)

mapper.update:
UPDATE gbl_status SET
gbl_preperation = 1
gbl_bookinglist = 1
gbl_truckmanifast = 1
gbl_invoice = #{invoice}
WHERE gbl_no = (select no from gbl where seq = #{seq});

UPDATE
	gbl
SET

		lbs = #{lbs}


		truck_seq = #{truckSeq}


		booking_seq = #{bookingSeq}

WHERE
	seq = #{seq}

UPDATE
	weight_certificate
SET

		truck_check = 1,
		truck_seq = #{truckSeq}


		booking_check = 1,
		booking_seq = #{bookingSeq}

WHERE
	gbl_seq = #{seq}

UPDATE
	decide
SET

		title = #{title},



			cost = #{cost}


			price = #{price}


WHERE
	gbl_seq = #{gblSeq}

	AND
		dd619_seq = #{dd619Seq}


	AND
		memorandum_list_seq = #{memorandumSeq}


	AND
		title = #{title}

mapper.delete:
DELETE FROM
	dd619
WHERE
	gbl_seq = #{gblSeq}
AND
	memorandum_list_seq = #{memorandumListSeq}
